	;description
Refresh PROC
	;clear screen
	                                  mov  ax, 4F02h
	                                  mov  bx, 0100h                        	; 640x400 screen graphics mode
	                                  INT  10h                              	;execute the configuration

	                                  mov  ax,0600h
	                                  mov  bh, 71h
	                                  mov  cx,0
	                                  mov  dx,184fh
	                                  int  10h                              	;using scrolling interrupt to clean the screen
    
	;calling the new frame elements
	                                  call DrawingBall
	                                  call DRAWE_PLAYER

	                                  mov  ah,86h
	                                  mov  dx,0FFh
	                                  mov  cx,0h
	                                  int  15h                              	;add delay
	                                  ret
Refresh ENDP

	;description
DrawingBall PROC
	                                  mov  dx,BallY
	                                  mov  cx,BallX
	                                  mov  si,offset BallColor              	;intialize the position & carry
	DrawRow:                          

	                                  mov  al,[si]                          	;mov current color
	                                  mov  ah,0ch
	                                  int  10h                              	;draw pixed mode

	                                  inc  cx
	                                  inc  si
	                                  mov  di,BallX
	                                  add  di,BallW
	                                  cmp  cx,di                            	;check end of current row
	                                  jNE  DrawRow

	                                  mov  cx,BallX
	                                  inc  dx
	                                  mov  di,BallY
	                                  add  di,BallH
	                                  cmp  dx,di
	                                  JNE  DrawRow                          	;check end of all rows

	                                  ret
DrawingBall ENDP

	;description
CheckBallCollisionWithScreen PROC


	;check collision with right edge of the screen
	                                  mov  Ax,BallX
	                                  add  Ax,BallW
	                                  cmp  Ax,ScreenW
    
	                                  JNG  NoCollisionWithRightEdge
	                                  NEG  BallSpeedX
	NoCollisionWithRightEdge:         

	;check collision with left edge of the screen
	                                  mov  Ax,BallX
	                                  cmp  Ax,0
	                                  JNL  NoCollisionWithLeftEdge
	                                  NEG  BallSpeedx
	NoCollisionWithLeftEdge:          

	;check collision with upper edge of the screen
	                                  mov  Ax,BallY
	                                  cmp  Ax,0
	                                  JNL  NoCollisionWithUpperEdge
	                                  NEG  BallSpeedY
	NoCollisionWithUpperEdge:         

	;check collision with lower edge of the screen
	                                  mov  Ax,BallY
	                                  add  Ax,BallH
	                                  cmp  Ax,ScreenH
	                                  JNG  NoCollisionWithLowerEdge
	                                  NEG  BallSpeedY
	NoCollisionWithLowerEdge:         

CheckBallCollisionWithScreen ENDP

	;description
UpdateBallPosition PROC
	                                  mov  Ax,BallSpeedX
	                                  add  BallX,Ax
	                                  mov  Ax,BallSpeedY
	                                  add  BallY,Ax
	                                  ret
UpdateBallPosition ENDP

	;description
UpdatePlayer1Position PROC

	                                  mov  Bx,ScreenH
	                                  sub  Bx,Player1H
	                                  cmp  Player1Y,Bx
	                                  JE   ExitUpdatePlayer1Position
	                                  mov  Ax,Player1SpeedY
	                                  add  Player1Y,Ax
	
	ExitUpdatePlayer1Position:        
	                                  ret
UpdatePlayer1Position ENDP

	;description
CheckBallCollisionWithPlayer1 PROC
	;first check the x axis maxx1>minx2 && maxx2>minx1
	                                  mov  Ax,BallX
	                                  add  Ax,BallW
	                                  mov  Bx ,Player1X
	                                  cmp  Ax,Bx
	                                  JNG  ExitCheckBallCollisionWithPlayer1

	                                  mov  Ax,Player1X
	                                  add  Ax,Player1W
	                                  mov  Bx,BallX
	                                  cmp  Ax,Bx
	                                  JNG  ExitCheckBallCollisionWithPlayer1

	;first check the y axis maxy1>miny2 && maxy2>miny1

	                                  mov  Ax,BallY
	                                  add  Ax,BallH
	                                  mov  Bx ,Player1Y
	                                  cmp  Ax,Bx
	                                  JNG  ExitCheckBallCollisionWithPlayer1

	                                  mov  Ax,Player1Y
	                                  add  Ax,Player1H
	                                  mov  Bx,BallY
	                                  cmp  Ax,Bx
	                                  JNG  ExitCheckBallCollisionWithPlayer1

	                                  NEG  BallSpeedX
	                                  NEG  BallSpeedY

	ExitCheckBallCollisionWithPlayer1:
	                                  ret
CheckBallCollisionWithPlayer1 ENDP

