.CODE
MAIN PROC FAR
	             mov  ax, @data
	             mov  DS, ax
	             mov  ax, 4F02h         	
	             mov  bx, 0100h         	; 640x400 screen graphics mode
	             INT  10h               	;execute the configuration
	             call DRAWE_PLAYER
	CHECK:       mov  ah,1                  ;Get key pressed Don't Wait for the key
	             int  16h					
	             jz   CHECK

	             call SHIFT_PLAYER         ; Clear player in prev position

	             cmp  ah,72                ; Check key pressed the update position
	             jz   MoveUp

	             cmp  ah,80
	             jz   MoveDown

	             cmp  ah,75
	             jz   MoveLeft

	             cmp  ah,77
	             jz   MoveRight

	ReadKey:     
	             mov  ah,0
	             int  16h
	             call DRAWE_PLAYER
	             jmp  CHECK

	MoveUp:      
	             SUB imgY_move, 1
	             jmp  ReadKey

	MoveDown:    
	             ADD imgY_move,1
	             jmp  ReadKey

	MoveLeft:    
	             SUB imgX_move,1
	             jmp  ReadKey

	MoveRight:   
	             ADD imgX_move,1

				
					call Refresh
    				call CheckBallCollisioinWithScreen
    				call UpdateBallPosition

	             jmp  ReadKey

	                
  
	             mov  dl,10
	             int  21h
            
MAIN ENDP
DRAWE_PLAYER PROC
	      MOV AH,0Bh        	;set the configuration
	       MOV CX, imgW  	    ;set the width (X) up to 64 (based on image resolution)
	       MOV DX, imgH 	    ;set the hieght (Y) up to 64 (based on image resolution)
		   mov DI, offset img   ; to iterate over the pixels
	       jmp Start    	    ;Avoid drawing before the calculations
	Drawit:
	       MOV AH,0Ch   	    ;set the configuration to writing a pixel
           mov al, [DI]         ; color of the current coordinates
		   cmp al ,31
		   JNZ valid
		   jmp start
	valid: 
	       ADD CX, imgX_move    ; for shifting in the x -axis
		   ADD DX, imgY_move    ; for shifting in the y-axis
	       MOV BH,00h   	    ;set the page number
	       INT 10h      	    ;execute the configuration
		   SUB CX ,imgX_move    ; return to our CX to validate our loop which get out in (0 ,0)
		   SUB DX ,imgY_move
	Start: 
		   inc DI
	       DEC Cx       	    ;  loop iteration in x direction
	       JNZ Drawit      	    ;  check if we can draw c urrent x and y and excape the y iteration
	       mov Cx, imgW 	    ;  if loop iteration in y direction, then x should start over so that we sweep the grid
	       DEC DX       	    ;  loop iteration in y direction
	       JZ  ENDING        	;  both x and y reached 00 so end program
		   Jmp Drawit

	ENDING:
	             ret
DRAWE_PLAYER ENDP
SHIFT_PLAYER PROC
	                push AX
	                
	   MOV AH,0Bh   	
	       MOV CX, imgW  	;
	       MOV DX, imgH 	;set the hieght (Y) up to 64 (based on image resolution)
		   mov DI, offset img  ; to iterate over the pixels
	       jmp StartC    	;Avoid drawing before the calculations
	CLEAN:
		   ADD CX, imgX_move  ; for shifting in the x -axis
		   ADD DX, imgY_move  ; for shifting in the y-axis	
	       MOV AH,0Ch   	;set the configuration to writing a pixel
           mov al, 00     ; color of the current coordinates
	       MOV BH,00h   	;set the page number
	       INT 10h      	;execute the configuration
		   SUB CX ,imgX_move  ; return to our CX to validate our loop which get out in (0 ,0)
		   SUB DX ,imgY_move
		   
	StartC: 
		   inc DI
	       DEC Cx       	
	       JNZ CLEAN      	;  check if we can draw c urrent x and y and excape the y iteration
	       mov Cx, imgW 	;  if loop iteration in y direction, then x should start over so that we sweep the grid
	       DEC DX       	;  loop iteration in y direction
	       JZ  FINAL      	;  both x and y reached 00 so end program
		   Jmp CLEAN
		FINAL :	
	                mov  SI,offset img 
	                pop  AX
	                ret
SHIFT_PLAYER ENDP    

;description
Refresh PROC
    mov ax,0600h
    mov bh, 0DFh
    mov cx,0
    mov dl,79
    mov dh,24
    int 10h             ;using scrolling interrupt to clean the screen
    

    call DrawingBall   ;calling the new frame elements
    
    mov ah,86h      
    mov dx,0FFh
    mov cx,0h
    int 15h             ;add delay
    ret
Refresh ENDP

;description
DrawingBall PROC
    mov dx,BallY
    mov cx,BallX
    mov si,offset BallColor   ;intialize the position & carry
DrawRow:

    mov al,[si]         ;mov current color
    mov ah,0ch          
    int 10h             ;draw pixed mode

    inc cx
    inc si
    mov di,BallX
    add di,BallW
    cmp cx,di         ;check end of current row
    jNE DrawRow

    mov cx,BallX
    inc dx
    mov di,BallY
    add di,BallH
    cmp dx,di
    JNE DrawRow         ;check end of all rows

    ret
DrawingBall ENDP

;description
CheckBallCollisioinWithScreen PROC

;first check the x axis maxx1>minx2&&maxx2>minx1

;check collision with right edge of the screen
    mov Ax,BallX
    add Ax,BallW
    cmp Ax,320
    
    JNG NoCollisionWithRightEdge
    NEG BallSpeedX
    NoCollisionWithRightEdge:

;check collision with left edge of the screen
    mov Ax,BallX
    cmp Ax,0
    JNL NoCollisionWithLeftEdge
    NEG BallSpeedx
    NoCollisionWithLeftEdge:

;check collision with upper edge of the screen
    mov Ax,BallY
    cmp Ax,0
    JNL NoCollisionWithUpperEdge
    NEG BallSpeedY
    NoCollisionWithUpperEdge:

;check collision with lower edge of the screen
    mov Ax,BallY
    add Ax,BallH
    cmp Ax,200
    JNG NoCollisionWithLowerEdge
    NEG BallSpeedY
    NoCollisionWithLowerEdge:

CheckBallCollisioinWithScreen ENDP

;description
UpdateBallPosition PROC
    mov Ax,BallSpeedX
    add BallX,Ax
    mov Ax,BallSpeedY
    add BallY,Ax
    ret
UpdateBallPosition ENDP



