	n1:                               cmp   username,'z'
	                                  jbe   v2
	                                  mov   bh,0
	                                  jmp   n2
	v2:                               mov   bh,1

	n2:                               and   bh,bl

	                                  cmp   username, 'A'
	                                  jae   v3
	                                  mov   cl,0
	                                  jmp   n3
	v3:                               mov   cl,1

	n3:                               cmp   username,'Z'
	                                  jbe   v4
	                                  mov   ch,0
	                                  jmp   n4
	v4:                               mov   ch,1

	n4:                               and   ch,cl

	                                  or    bh,ch
	                                  jnz   validated

